            <script>
                var form = document.querySelector('#form');
                var title = document.querySelector('#title');
                var description = document.querySelector('#description');

                function showError(input, message) {
                    let small = input.parentElement.querySelector('small');
                    input.parentElement.classList.add('error');
                    small.innerText = message;
                }

                function showSuccess(input) {
                    let small = input.parentElement.querySelector('small');
                    input.parentElement.classList.remove('error');
                    small.innerText = '';
                }

                function checkValidate(listInput) {
                    let check = true;
                    listInput.value = listInput.value.trim();
                    if (listInput.value == "") {
                        check = false;
                        showError(listInput, "KhÃ´ng Ä‘Æ°á»£c bá» trá»‘ng...");
                    } else {
                        check = checkLength(listInput, 3, 20);
                    }
                    return check;
                }

                function checkEditor(editor) {
                    let check = true;
                    let value = editor.getData().split("&nbsp;")
                    let string = "";
                    for (let i = 0; i < value.length; i++) {
                        if (value[i] != " ") {
                            string += value[i];
                        }
                    }
                    console.log(string);
                    if (string == "") {
                        check = false;
                        let small = document.querySelector('.small-editor');
                        //input.parentElement.classList.add('error');
                        small.innerText = "KhÃ´ng Ä‘Æ°á»£c bá» trá»‘ng...";
                    } else {
                        let small = document.querySelector('.small-editor');
                        // input.parentElement.classList.remove('error');
                        small.innerText = '';
                    }
                    return check;
                }

                function checkLength(input, min, max) {
                    let check = true;
                    input.value = input.value.trim();
                    if (input.value.length < min) {
                        check = false
                        showError(input, `Pháº£i cÃ³ Ã­t nháº¥t ${min} kÃ­ tá»±...`);

                    } else if (input.value.length > max) {
                        check = false
                        showError(input, `KhÃ´ng Ä‘Æ°á»£c quÃ¡ ${max} kÃ­ tá»±...`)

                    } else {
                        showSuccess(input)
                    }
                    return check;
                }

                //console.log(checkValidate([description]));

                form.addEventListener('submit', function(e) {

                    let check2 = checkEditor(editor);
                    let check1 = checkValidate(description);
                    if (!checkValidate(title) || !check1 || !check2) {
                        e.preventDefault();

                    }
                });



                // form.addEventListener('submit', function(e) {

                //     let check = checkValidate([title]);
                //     if (!check) {
                //         e.preventDefault();
                //     }
                // })
            </script>
